mapper.select:
select count(*) from tbl_draft where draft_saved = 0 and fk_member_userid = #{member_userid}
and lower(draft_form_type) like '%' || lower(#{searchWord}) ||'%'
and lower(draft_subject) like '%' || lower(#{searchWord}) ||'%'

select count(*) from tbl_draft where draft_saved = 1 and fk_member_userid = #{member_userid}
and lower(draft_form_type) like '%' || lower(#{searchWord}) ||'%'
and lower(draft_subject) like '%' || lower(#{searchWord}) ||'%'

mapper.update:
update tbl_approval set approval_feedback = #{approval_feedback}
                      , approval_status = '승인'
                      , approval_date = sysdate
where fk_draft_no = #{fk_draft_no} and fk_member_userid = #{member_userid}

update tbl_approval set approval_status = '승인'
					  , approval_date = sysdate
where fk_draft_no = #{fk_draft_no} and fk_member_userid = #{member_userid}

update tbl_approval set approval_feedback = #{approval_feedback}
                      , approval_status = '반려'
                      , approval_date = sysdate
where fk_draft_no = #{fk_draft_no} and fk_member_userid = #{member_userid}

mapper.select:
select count(*) from tbl_draft D join tbl_approval A on D.draft_no = A.fk_draft_no where draft_saved = 0 and approval_step = 0 and draft_status = '승인완료' and A.fk_member_userid = #{member_userid}
and lower(draft_form_type) like '%' || lower(#{searchWord}) ||'%'
and lower(draft_subject) like '%' || lower(#{searchWord}) ||'%'
