mapper.select:
select * from ( select rownum as rno, notice_no, fk_member_userid, fk_child_dept_no, notice_dept, case when length(notice_title) > 23 then substr(notice_title,1,30) || ' ...' else notice_title end as notice_title, notice_content, notice_fix, notice_del, notice_view_cnt, notice_filename, to_char(notice_write_date, 'yyyy-mm-dd') as notice_write_date from tbl_notice a join tbl_member b on a.fk_member_userid = b.member_userid where notice_dept in
#{parent_dept_no}
and MEMBER_LAST IS NULL order by notice_fix desc, notice_write_date desc ) where rno BETWEEN 1 AND 4

select m.fk_member_userid, m.alarm_no, a.approval_status, null notice_dept, m.alarm_category, case when length(d.draft_subject) > 20 then substr(d.draft_subject,1,15) || ' ..' else d.draft_subject end as alarm_title, m.alarm_cateno, to_char(m.alarm_at, 'yyyy-mm-dd') as alarm_at, m.alarm_is_read from tbl_alarm m join tbl_approval a on m.alarm_cateno = a.fk_draft_no join tbl_draft d on a.fk_draft_no = d.draft_no where m.fk_member_userid = #{member_userid} and a.approval_status = '결재예정' and a.approval_step != 0
union all
select m.fk_member_userid, m.alarm_no, null as approval_status, n.notice_dept, m.alarm_category, case when length(n.notice_title) > 20 then substr(n.notice_title,1,15) || ' ..' else n.notice_title end as alarm_title, m.alarm_cateno, to_char(m.alarm_at, 'yyyy-mm-dd') as alarm_at, m.alarm_is_read from tbl_alarm m join tbl_notice n on m.alarm_cateno = n.notice_no join tbl_member u on n.fk_member_userid = u.member_userid where m.fk_member_userid = #{member_userid}
